import React from "react";
const { useState, useEffect } = React;

const Session = () => {
  let [info, setInfo] = useState({
    nombre: "",
    perrosFavoritos: [],
  });

  const getUser = async () => {
    let responseFromGet = await fetch("http://localhost:3000/usuario", {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        token: window.localStorage.token,
      },
    })
      .then((response) => response.json())
      .then((result) => {
        return result;
      });
    console.log(responseFromGet);
    let username = responseFromGet.nombre;
    let favoriteDogs = responseFromGet.perrosFavoritos;
    console.log(favoriteDogs);
    let perros = favoriteDogs.map((perro) => {
      let dogObject = {
        id: perro._id,
        name: perro.nombre,
        raza: perro.raza,
      };
      return dogObject;
    });
    let userInfo = {
      nombre: username,
      perrosFavoritos: perros,
    };
    setInformation(userInfo);
  };

  const setInformation = (information) => {
    setInfo({
      nombre: information.nombre,
      perrosFavoritos: information.perrosFavoritos,
    });
  };

  useEffect(() => {
    getUser();
  }, []);

  if (info.usuario === "") {
    return <div>Cargando...</div>;
  } else {
    return (
      <div>
        <p>Welcome {info.nombre}</p>
        {console.log(info.perrosFavoritos)}
        {/* {info.perrosFavoritos.map((perro) => {
          return <p>{perro.id}</p>;
        })} */}
      </div>
    );
  }
};

export default Session;
