import React from "react";
const { useState, useEffect } = React;

const HomeVeterinario = () => {
  let [info, setInfo] = useState({
    nombre: "",
    perros: [],
    rol: "",
  });

  const getUser = async () => {
    let responseFromGet = await fetch("http://localhost:3000/veterinario", {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        token: window.localStorage.token,
      },
    })
      .then((response) => response.json())
      .then((result) => {
        return result;
      });
    let rol = responseFromGet.rol;
    let username = responseFromGet.nombre;
    let perrosVet = responseFromGet.perros;
    // let perros = perrosVet.map((perro) => {
    //   let dogObject = {
    //     id: perro._id,
    //     nombre: perro.nombre,
    //     raza: perro.raza,
    //   };
    //   return dogObject;
    // });

    let userInfo = {
      nombre: username,
      perros: perrosVet,
      rol: rol,
    };
    setInformation(userInfo);
  };

  const setInformation = (information) => {
  setInfo({

      nombre: information.nombre,
      perros: information.perros,
      rol: information.rol,
    });
  };

  useEffect(() => {
    getUser();
  }, []);
  console.log(info);
  if (info.usuario === "") {
    return <div>Cargando...</div>;
  } else if (info.rol == false) {
    return (<h1>You are not permitted here.</h1>);
  } else if (info.rol == true){
    return (
      <div>
        {console.log(info)}
        <p>Welcome {info.nombre}</p>
        <h5>Tus perros son:</h5>
        <table className="table table-striped table-dark">
          <tbody>
            {info.perros.map((perro) => {
              return (
                <tr key={`perro-container-${perro.id}`}>
                  <td key={`perro-name-${perro.id}`}>{perro.nombre}</td>
                  <td key={`perro-raza-${perro.id}`}>{perro.raza}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
};

export default HomeVeterinario;
