import React from "react";
import { Link } from "react-router-dom";
import { useHistory } from "react-router";
const useState = React.useState;

const Login = () => {
  let [info, setInfo] = useState({
    email: "",
    password: "",
  });
  let history = useHistory();

  const postLogin = async () => {
    let responseFromPost = await fetch("http://localhost:3000/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: info.email,
        password: info.password,
      }),
    })
      .then((response) => response.json())
      .then((result) => {
        return result;
      });
    window.localStorage.setItem("token", responseFromPost.token);
    let token = responseFromPost.token;
      history.push("/veterinario");

  console.log("fin de login")
  return

}

  const handleChange = (event) => {
    event.preventDefault();
    setInfo({
      ...info,
      [event.target.name]: event.target.value,
      [event.target.name]: event.target.value,
    });
  };

  const getResponse = () => {
    console.log("funciona");
    fetch("http://localhost:3000/veterinarios")
      .then((res) => res.json())
      .then((result) => {
        console.log(result);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    postLogin();
  };

  return (
    <div>
      {console.log(info)}
      <h1 className="text-center">Login</h1>
      <div className="form">
        <form onSubmit={handleFormSubmit}>
          <div className="mb-3">
            <label className="form-label">Email address</label>
            <input
              name="email"
              type="email"
              className="form-control"
              id="exampleInputEmail1"
              onChange={handleChange}
            />
            <div id="emailHelp" className="form-text">
              We'll never share your email with anyone else.
            </div>
          </div>
          <div className="mb-3">
            <label className="form-label">Password</label>
            <input
              name="password"
              type="password"
              className="form-control"
              id="exampleInputPassword1"
              onChange={handleChange}
            />
          </div>

          <div className="d-grid gap-2 col-3 mx-auto">
            <button type="submit" className="btn btn-secondary">
              Submit
            </button>
            <button
              type="button"
              className="btn btn-success"
              onClick={getResponse}
            >
              Get Response
            </button>
            <Link type="submit" className="btn btn-primary btn-sm" to="/">
              Go Back
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
